// Fuente Roboto
$(document).ready(function(){
    WebFontConfig = {
        google:{families:['Roboto:400,300:latin']}
    };
    (function() {
        var wf = document.createElement('script');
        wf.src = 'https://ajax.googleapis.com/ajax/libs/webfont/1/webfont.js';
        wf.type = 'text/javascript';
        wf.async = 'true';
        var s = document.getElementsByTagName('script')[0];
        s.parentNode.insertBefore(wf, s);
    })();
})
 
// Collapse
$(document).ready(function() {
    var open = 1;
    $('.option').click(function () {
        $option = $(this);
        $collapse = $option.next();
        $collapse.slideToggle(500, function () {
            if (open==1) {
                $("div.i-down1").addClass("arrow-up"); 
                open = 0;
            }
            else {
                $("div.i-down1").removeClass("arrow-up");
                open = 1;
            }
        });
    });
})

$(document).ready(function() {
    var open = 1;

    $('.option2').click(function () {
        $option = $(this);
        $collapse2 = $option.next();
        $collapse2.slideToggle(500, function () {
            if (open==1) {
                $("div.icon-wobble").addClass("up");
                open = 0;
            }
            else {
                $("div.icon-wobble").removeClass("up");
                open = 1;
            }
        });
    });
})

