'use strict';

jQuery(document).ready(function ($) {

    var lastId,
    topMenu = $("#top-navigation"),
    topMenuHeight = topMenu.outerHeight(),
        // All list items
        menuItems = topMenu.find("a"),
        // Anchors corresponding to menu items
        scrollItems = menuItems.map(function () {
            var href = $(this).attr("href");
            if(href.indexOf("#") === 0){
                var item = $($(this).attr("href"));
                if (item.length) {
                    return item;
                }
            }
        });

    // Bind to scroll
    $(window).scroll(function () {

        //Display or hide scroll to top button 
        if ($(this).scrollTop() > 100) {
            $('.scrollup').fadeIn();
        } else {
            $('.scrollup').fadeOut();
        }

        if ($(this).scrollTop() > 91) {
            $('.navbar').addClass('animated fadeInDown menu2');
        } else {
            $('.navbar').removeClass('animated fadeInDown menu2');
        }
		
    });

    // Function for scroliing to top
    $('.scrollup').click(function () {
        $("html, body").animate({
            scrollTop: 0
        }, 600);
        return false;
    });

    // Slidebar menu function
    $(document).ready(function() {
		$.slidebars({
			siteClose: true, // true or false
			disableOver: 979, // integer or false
			hideControlClasses: false, // true or false
			scrollLock: true // true or false
		});
	});

    // Show and hide traveler selection
    
    /*
    $(function() {
      $(".trigger").on("click", function(e) {
        $(".traveler-selection").addClass("wide");
        e.stopPropagation()
      });

      $(document).on("click", function(e) {
        if ($(e.target).is(".traveler-selection") === false) {
          $(".traveler-selection").removeClass("wide");
        }
      });
    });
    */
    
    $(function() {
      $(".trigger").on("click", function(e) {
        $(".traveler-selection").toggleClass("wide");
      });
      $(document).on("click", function(e) {
        if ($(e.target).is(".traveler-selection, .trigger") === false) {
          $(".traveler-selection").removeClass("wide");
        }
      });
    });
    
});



